export default {
  name: "SelectAnio",
  props: ["grafica"],
  data: function data() {
    return {
      anioActual: new Date().getFullYear(),
      anios: []
    };
  },
  mounted: function mounted() {
    for (var i = 2015; i <= this.anioActual; i++) {
      this.anios.push(i);
    }
  },
  methods: {
    seleccionar: function seleccionar() {
      var datos = {
        noGrafica: this.grafica,
        anio: this.anioActual
      };
      this.$emit("seleccionado", datos);
    }
  }
};
